PREFIX qpr: <http://istresearch.com/qpr>
SELECT ?phone (count(?ad) AS ?count)
WHERE
{	?ad a qpr:Ad;
	qpr:location 'Victoria, BC';
	qpr:ethnicity 'asian';
	qpr:phone ?phone
}
GROUP BY ?phone
ORDER BY DESC(?count)
LIMIT 1

